data.articleId=result;
getPostUrl(data,'qryMagArticleDetail');
}else{
data.chapterId=result;
if(java.get('type')=='4'){
getPostUrl(data,'qryFileInfo');}
else{getPostUrl(data,'qryChapterDetails')}
}

